<?php/** * Register Widgets */function zr_plugin_register(){	register_widget( 'zr_testimonial_slider_widget' );	register_widget( 'zr_ourteam_slider_widget' );	register_widget( 'zr_instagram_widget' );}add_action( 'widgets_init', 'zr_plugin_register' );include_once( plugin_dir_path( __FILE__ ) . 'zr-ourteam/zr-ourteam.php' );include_once( plugin_dir_path( __FILE__ ) . 'zr-testimonial/zr-testimonial.php' );include_once( plugin_dir_path( __FILE__ ) . 'zr-instagram/zr-instagram.php' );if( !class_exists('zr_resp_slider') ) :	include_once( plugin_dir_path( __FILE__ ) . 'zr-responsive-post/zr-resp-slider.php' );endif;/*** Shortcode Blog*/$zr_blogcol = 0;function zr_blog( $atts, $content = '' ){	extract( shortcode_atts(		array(			'title' => '',			'description' =>'',			'category' => '',			'orderby' => '',			'order'	=> '',			'numberposts' => 5,			'columns' => 1,			'layout' => 'list'		), $atts )	);	global $zr_blogcol;	$zr_blogcol = $columns;	ob_start();?>	<div class="category-contents">		<?php if( $title != '' || $description != '' ) : ?>		<div class="zrblog-title">			<?php echo ( $title != '' ) ? '<h2>' . $title . '</h2>' : ''; ?>			<?php echo ( $description != '' ) ? '<div class="zrblog-description">' . $description . '</div>' : ''; ?>		</div>		<?php endif; ?>		<?php 			$blogclass = 'blog-content blog-content-'. $layout;			if( $layout == 'grid' ){				$blogclass .= ' row';			}		?>		<div class="<?php echo esc_attr( $blogclass ) ?>">		<?php 			$paged 	 = ( get_query_var('paged') ) ? get_query_var('paged') : 1;				$default = array( 				'post_type'	=> 'post',				'orderby'	=> $orderby,				'order'	=> $order,				'paged' => $paged,				'showposts'	=> $numberposts			);			if( $category != '' ) :				$default['tax_query'] = array(					array(						'taxonomy'	=> 'category',						'field'	=> 'slug',						'terms'	=> $category					)				);			endif;			$list = new WP_Query( $default );			while( $list->have_posts() ) : $list->the_post();				if( locate_template( 'templates/content-' . $layout . '.php' ) ) :					get_template_part( 'templates/content', $layout ); 				else:						echo '';			endif;			endwhile;			wp_reset_postdata();		?>		</div>		<?php if ($list->max_num_pages > 1) : ?>			<div class="pagination nav-pag pull-right">			<?php				echo paginate_links( array(					'base' => esc_url_raw( str_replace( 999999999, '%#%', get_pagenum_link( 999999999, false ) ) ),					'format' => '',					'current' => max( 1, get_query_var('paged') ),					'total' => $list->max_num_pages,					'end_size' => 2,					'mid_size' => 2,					'prev_text' => '<i class="fa fa-angle-left"></i>',					'next_text' => '<i class="fa fa-angle-right"></i>',					'type' => 'list',									) );			?>			</div>			<?php endif; ?>	</div><?php 	$content = ob_get_clean();	return $content;}add_shortcode( 'zr_blog', 'zr_blog' );